mapper.select:

select * from ad_banner

  is_delete = 0

    and show_location = #{showLocation,jdbcType=TINYINT}


    and banner_title = #{bannerTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link_type = #{linkType,jdbcType=TINYINT}


    and outside_link = #{outsideLink,jdbcType=VARCHAR}


    and inside_link_type = #{insideLinkType,jdbcType=TINYINT}


    and inside_link_id = #{insideLinkId,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from ad_banner

  is_delete = 0

    and show_location = #{showLocation,jdbcType=TINYINT}


    and banner_title = #{bannerTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link_type = #{linkType,jdbcType=TINYINT}


    and outside_link = #{outsideLink,jdbcType=VARCHAR}


    and inside_link_type = #{insideLinkType,jdbcType=TINYINT}


    and inside_link_id = #{insideLinkId,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


limit #{pageStartNum},#{pageSize}


select count(*) from ad_banner

  is_delete = 0

    and show_location = #{showLocation,jdbcType=TINYINT}


    and banner_title = #{bannerTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link_type = #{linkType,jdbcType=TINYINT}


    and outside_link = #{outsideLink,jdbcType=VARCHAR}


    and inside_link_type = #{insideLinkType,jdbcType=TINYINT}


    and inside_link_id = #{insideLinkId,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}

select * from ad_banner

  is_delete = 0

    and show_location = #{showLocation,jdbcType=TINYINT}


    and banner_title like concat(concat('%',#{bannerTitle,jdbcType=VARCHAR}),'%')


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link_type = #{linkType,jdbcType=TINYINT}


    and outside_link = #{outsideLink,jdbcType=VARCHAR}


    and inside_link_type = #{insideLinkType,jdbcType=TINYINT}


    and inside_link_id = #{insideLinkId,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


 order by sorting
