mapper.select:
select * from ad_startup_page where st_page_id in
#{item}
and is_delete = 0

mapper.update:

update ad_startup_page
set is_delete = 1
where st_page_id = #{stPageId,jdbcType=BIGINT}


update ad_startup_page
set is_enable = #{enable}
where st_page_id in

  #{item}

and is_delete = 0

mapper.select:

select * from ad_startup_page

  is_delete = 0

    and page_title = #{pageTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link = #{link,jdbcType=VARCHAR}


    and start_time = #{startTime,jdbcType=TIMESTAMP}


    and end_time = #{endTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from ad_startup_page

  is_delete = 0

    and page_title = #{pageTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link = #{link,jdbcType=VARCHAR}


    and start_time = #{startTime,jdbcType=TIMESTAMP}


    and end_time = #{endTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


limit #{pageStartNum},#{pageSize}


select count(*) from ad_startup_page

  is_delete = 0

    and page_title = #{pageTitle,jdbcType=VARCHAR}


    and pic_path = #{picPath,jdbcType=VARCHAR}


    and sorting = #{sorting,jdbcType=BIGINT}


    and link = #{link,jdbcType=VARCHAR}


    and start_time = #{startTime,jdbcType=TIMESTAMP}


    and end_time = #{endTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}
