mapper.select:

select * from advisory_tag

  is_delete = 0

    and type = #{type,jdbcType=TINYINT}


    and phrase = #{phrase,jdbcType=VARCHAR}


    and ref_count = #{refCount,jdbcType=INTEGER}


    and hit_count = #{hitCount,jdbcType=INTEGER}


    and order_no = #{orderNo,jdbcType=INTEGER}


    and is_enable = #{isEnable,jdbcType=INTEGER}

  order by order_no asc


