mapper.select:
select * from article_collection where col_id in
#{item}
and is_delete = 0

mapper.update:

update article_collection
set is_delete = 1
where col_id = #{colId,jdbcType=BIGINT}

mapper.select:

select * from article_collection

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and collection_time = #{collectionTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


select * from article_collection

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and collection_time = #{collectionTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


limit #{pageStartNum},#{pageSize}


select count(*) from article_collection

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and collection_time = #{collectionTime,jdbcType=TIMESTAMP}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}
