mapper.select:

select * from article

  is_delete = 0

    and category_id = #{categoryId,jdbcType=BIGINT}


    and type_id = #{typeId,jdbcType=BIGINT}


    and title = #{title,jdbcType=VARCHAR}


    and digest = #{digest,jdbcType=VARCHAR}


    and hits = #{hits,jdbcType=BIGINT}


    and collection_num = #{collectionNum,jdbcType=BIGINT}


    and author = #{author,jdbcType=VARCHAR}


    and source = #{source,jdbcType=VARCHAR}


    and thumbnail = #{thumbnail,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and share = #{share,jdbcType=INTEGER}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


