select count(*) from article where article_id in
#{item}
and is_delete = 0 and is_enable = 0

		SELECT t1.*, t2.type_name
		FROM
		article t1, article_type t2
		WHERE t1.is_delete=0
		AND t2.type_name=#{typeName}
		AND t1.type_id = t2.type_id

            AND t1.is_enable=#{isEnable}

		ORDER BY t1.create_time desc
		LIMIT 0,#{size}

		SELECT t1.*, t2.*,t3.*
		FROM
		article t1, article_type t2, article_category t3
		WHERE t1.is_delete=0
		AND t2.type_name=#{typeName}
		AND t1.type_id = t2.type_id
		AND t3.category_name=#{categoryName}
		AND t1.category_id=t3.category_id

            AND t1.is_enable=#{isEnable}

		ORDER BY t1.hits desc
		LIMIT 0,#{size}

select * from article where article_id in

	#{id}

and is_delete = 0 and is_enable = 0

	limit #{pageStartNum},#{pageSize}

