where pcid = #{pcid,jdbcType=BIGINT}

mapper.select:

select * from capital_platform_cash

  is_delete = 0

    and cash_balance = #{cashBalance,jdbcType=DECIMAL}


    and income_amount = #{incomeAmount,jdbcType=DECIMAL}


    and expenses_amount = #{expensesAmount,jdbcType=DECIMAL}


    and top_up_amount = #{topUpAmount,jdbcType=DECIMAL}


    and withdraw_amount = #{withdrawAmount,jdbcType=DECIMAL}


select * from capital_platform_cash

