mapper.select:

select * from eap_menu

  is_delete = 0

    and menu_type = #{menuType,jdbcType=TINYINT}


    and menu_name = #{menuName,jdbcType=VARCHAR}


    and menu_url = #{menuUrl,jdbcType=VARCHAR}


    and parent_menu_id = #{parentMenuId,jdbcType=BIGINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}

mapper.insert:
insert into eap_menu (menu_id, menu_type, menu_name, 
  menu_url, parent_menu_id, create_time, 
  modify_time, is_enable, is_delete
  )
values (#{menuId,jdbcType=BIGINT}, #{menuType,jdbcType=TINYINT}, #{menuName,jdbcType=VARCHAR}, 
  #{menuUrl,jdbcType=VARCHAR}, #{parentMenuId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{isEnable,jdbcType=TINYINT}, #{isDelete,jdbcType=TINYINT}
  )
