mapper.select:

select * from eap_user

  is_delete = 0

    and user_type = #{userType,jdbcType=TINYINT}


    and ee_id = #{eeId,jdbcType=BIGINT}


    and show_name = #{showName,jdbcType=VARCHAR}


    and mobile_phone = #{mobilePhone,jdbcType=VARCHAR}


    and username = #{username,jdbcType=VARCHAR}


    and eap_password = #{eapPassword,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from eap_user

  is_delete = 0

    and user_type = #{userType,jdbcType=TINYINT}


    and ee_id = #{eeId,jdbcType=BIGINT}


    and show_name = #{showName,jdbcType=VARCHAR}


    and mobile_phone like CONCAT(CONCAT('%',#{mobilePhone}),'%')


    and username = #{username,jdbcType=VARCHAR}


    and eap_password = #{eapPassword,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


order by user_type DESC limit #{pageStartNum},#{pageSize}


select count(*) from eap_user

  is_delete = 0

    and user_type = #{userType,jdbcType=TINYINT}


    and ee_id = #{eeId,jdbcType=BIGINT}


    and show_name = #{showName,jdbcType=VARCHAR}


    and mobile_phone = #{mobilePhone,jdbcType=VARCHAR}


     and mobile_phone like CONCAT(CONCAT('%',#{mobilePhone}),'%')


    and eap_password = #{eapPassword,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}
