where advisory_id = #{advisoryId,jdbcType=BIGINT}

mapper.select:

select * from member_advisory

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and title = #{title,jdbcType=VARCHAR}


    and content = #{content,jdbcType=VARCHAR}


    and write_location = #{writeLocation,jdbcType=VARCHAR}


    and is_anony = #{isAnony,jdbcType=TINYINT}


    and read_count = #{readCount,jdbcType=INTEGER}


    and share_count = #{shareCount,jdbcType=INTEGER}


    and is_recommended = #{isRecommended,jdbcType=TINYINT}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and order_factor = #{orderFactor,jdbcType=INTEGER}


    and release_from = #{releaseFrom,jdbcType=BIGINT}


