select member_advisory.* from member_advisory
inner join member using(mid)

	member_advisory.is_delete = 0

		and (member.nickname like concat('%',#{words},'%') or 
		member_advisory.content like concat('%',#{words},'%'))


		AND member_advisory.create_time >= #{begin}


		AND member_advisory.create_time <= #{end}


order by member_advisory.create_time desc	
limit #{pageStartNum},#{pageSize}

select count(*) from member_advisory inner join member using(mid)
member_advisory.is_delete = 0
and (member.nickname like concat('%',#{words},'%') or member_advisory.content like concat('%',#{words},'%'))
AND member_advisory.create_time >= #{begin}
AND member_advisory.create_time <= #{end}

SELECT ma.*,t1.nickname,t1.mobile_phone FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN member_advisory ma ON t1.mid = ma.mid where ma.is_delete=0
and (t1.nickname like concat('%',#{words},'%') or ma.content like concat('%',#{words},'%'))
AND ma.create_time >= #{begin}
AND ma.create_time <= #{end}
and ma.mid=#{mid}
order by ma.create_time desc limit #{pageStartNum},#{pageSize}

SELECT count(*) FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN member_advisory ma ON t1.mid = ma.mid where ma.is_delete=0
and (t1.nickname like concat('%',#{words},'%') or ma.content like concat('%',#{words},'%'))
AND ma.create_time >= #{begin}
AND ma.create_time <= #{end}
and ma.mid=#{mid}
