mapper.select:

select * from member_update_comment

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and update_id = #{updateId,jdbcType=BIGINT}


    and is_anony = #{isAnony,jdbcType=TINYINT}


    and comment_content = #{commentContent,jdbcType=VARCHAR}


    and write_location = #{writeLocation,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=BIGINT}


    and comment_time = #{commentTime,jdbcType=TIMESTAMP}


    and read_status = #{readStatus,jdbcType=TINYINT}


