mapper.insert:

insert into member_update (update_id, mid, content, 
  embrace_num, is_anony, read_count, 
  write_location, create_time, modify_time, 
  update_type, release_from, is_delete
  )
values (#{updateId,jdbcType=BIGINT}, #{mid,jdbcType=BIGINT}, #{content,jdbcType=VARCHAR}, 
  #{embraceNum,jdbcType=BIGINT}, #{isAnony,jdbcType=TINYINT}, #{readCount,jdbcType=INTEGER}, 
  #{writeLocation,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, 
  #{updateType,jdbcType=INTEGER}, #{releaseFrom,jdbcType=BIGINT}, #{isDelete,jdbcType=TINYINT}
  )

mapper.select:
select * from member_update where update_id in
#{item}
and is_delete = 0

mapper.update:

update member_update
set is_delete = 1
where update_id = #{updateId,jdbcType=BIGINT}

