select member_update.* from member_update
inner join member using(mid)

	member_update.is_delete = 0

		and (member.nickname like concat('%',#{words},'%') or
		member_update.content like concat('%',#{words},'%'))


		AND member_update.create_time >= #{begin}


		AND member_update.create_time <= #{end}


order by member_update.create_time desc	
limit #{pageStartNum},#{pageSize}

select count(*) from member_update inner join member using(mid)
member_update.is_delete = 0
and (member.nickname like concat('%',#{words},'%') or member_update.content like concat('%',#{words},'%'))
AND member_update.create_time >= #{begin}
AND member_update.create_time <= #{end}

	select mu.* from member_update mu, member_concern mc
	where mu.is_delete=0 and mc.is_delete=0 
	  and mu.mid=mc.concern_to and mc.concern_from=#{mid}
	  and mu.is_anony=1
	  and release_from = #{releaseFrom}
  order by create_time desc
  limit #{pageStartNum},#{pageSize}

select count(*) from member_update mu, member_concern mc
where mu.is_delete=0 and mc.is_delete=0 
  and mu.mid=mc.concern_to and mc.concern_from=#{mid}
  and mu.is_anony=1
  and release_from = #{releaseFrom}

SELECT mu.* FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN member_update mu ON t1.mid = mu.mid where mu.is_delete=0
and (t1.nickname like concat('%',#{words},'%') or mu.content like concat('%',#{words},'%'))
AND mu.create_time >= #{begin}
AND mu.create_time <= #{end}
AND mu.mid = #{mid}
order by mu.create_time desc limit #{pageStartNum},#{pageSize}

SELECT count(*) FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN member_update mu ON t1.mid = mu.mid where mu.is_delete=0
and (t1.nickname like concat('%',#{words},'%') or mu.content like concat('%',#{words},'%'))
AND mu.create_time >= #{begin}
AND mu.create_time <= #{end}
AND mu.mid = #{mid}
order by mu.create_time desc
