mapper.select:
select * from message_push where mp_id in
#{item}
and is_delete = 0

mapper.update:

update message_push
set is_delete = 1
where mp_id = #{mpId,jdbcType=BIGINT}


update message_push
set is_enable = #{enable}
where mp_id in

  #{item}

and is_delete = 0

mapper.select:

select * from message_push

  is_delete = 0

    and title = #{title,jdbcType=VARCHAR}


    and push_status = #{pushStatus,jdbcType=TINYINT}


    and push_type = #{pushType,jdbcType=TINYINT}


    and click_count = #{clickCount,jdbcType=BIGINT}


    and push_link = #{pushLink,jdbcType=VARCHAR}


    and link_text = #{linkText,jdbcType=VARCHAR}


    and push_time = #{pushTime,jdbcType=TIMESTAMP}


    and img = #{img,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


    and is_all = #{isAll,jdbcType=TINYINT}


    and pre_img = #{preImg,jdbcType=VARCHAR}


    and content = #{content,jdbcType=LONGVARCHAR}


select * from message_push

  is_delete = 0

    and title = #{title,jdbcType=VARCHAR}


    and push_status = #{pushStatus,jdbcType=TINYINT}


    and push_type = #{pushType,jdbcType=TINYINT}


    and click_count = #{clickCount,jdbcType=BIGINT}


    and push_link = #{pushLink,jdbcType=VARCHAR}


    and link_text = #{linkText,jdbcType=VARCHAR}


    and push_time = #{pushTime,jdbcType=TIMESTAMP}


    and img = #{img,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


    and is_all = #{isAll,jdbcType=TINYINT}


    and pre_img = #{preImg,jdbcType=VARCHAR}


    and content = #{content,jdbcType=LONGVARCHAR}


limit #{pageStartNum},#{pageSize}


select count(*) from message_push

  is_delete = 0

    and title = #{title,jdbcType=VARCHAR}


    and push_status = #{pushStatus,jdbcType=TINYINT}


    and push_type = #{pushType,jdbcType=TINYINT}


    and click_count = #{clickCount,jdbcType=BIGINT}


    and push_link = #{pushLink,jdbcType=VARCHAR}


    and link_text = #{linkText,jdbcType=VARCHAR}


    and push_time = #{pushTime,jdbcType=TIMESTAMP}


    and img = #{img,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


    and is_all = #{isAll,jdbcType=TINYINT}


    and pre_img = #{preImg,jdbcType=VARCHAR}


    and content = #{content,jdbcType=LONGVARCHAR}


select * from message_push

    is_delete=0

    and title like concat(concat('%',#{title,jdbcType=VARCHAR}),'%') 


    and push_status = #{pushStatus,jdbcType=TINYINT}


order by push_time desc
limit #{pageStartNum},#{pageSize}

mapper.update:
update message_push
set pushStatus = 2
where mp_id = #{mpId,jdbcType=BIGINT}

update message_push
set click_count = click_count + 1
where mp_id = #{mpId,jdbcType=BIGINT}
