  modify_time, is_enable, is_delete
  )
values (#{msId,jdbcType=BIGINT}, #{orderNo,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{historyPlayCount,jdbcType=INTEGER}, #{historyShareCount,jdbcType=INTEGER}, #{mcId,jdbcType=BIGINT}, 
  #{filePath,jdbcType=VARCHAR}, #{fileType,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{isEnable,jdbcType=TINYINT}, #{isDelete,jdbcType=TINYINT}
  )

