select * from music_song

  is_delete = 0

    and	(name like concat('%',#{words},'%')) 


    and mc_id = #{mcId,jdbcType=BIGINT}


order by history_play_count desc,ms_id desc
limit #{pageStartNum},#{pageSize}

	select count(*) from music_song song

    is_delete = 0

      and	(name like concat('%',#{words},'%')) 


      and mc_id = #{mcId,jdbcType=BIGINT}


    	and  history_play_count >= #{historyPlayCount} 


      and  (select coalesce(play_count, 0) from music_song_daily_statis
			where is_delete=0 and ms_id=song.ms_id
			and  DATE_FORMAT(record_date, '%Y%m%d') = DATE_FORMAT(now(), '%Y%m%d')
			) >= #{todayPlayCount} 


      and  (select coalesce(sum(play_count), 0) from music_song_daily_statis
			where is_delete=0 and ms_id = song.ms_id
			and DATE_FORMAT(record_date, '%Y%m%d') >= DATE_FORMAT(date_sub(now(), INTERVAL 6 DAY), '%Y%m%d')
			) >= #{weekPlayCount} 


      and  history_share_count >= #{historyShareCount}

select coalesce(sum(play_count), 0) from music_song_daily_statis
where is_delete=0 and ms_id = #{msId} 
and  DATE_FORMAT(record_date, '%Y%m%d') >= DATE_FORMAT(date_sub(now(), INTERVAL 6 DAY), '%Y%m%d')
