mapper.select:

select * from personality_characters_similarity

  is_delete = 0

    and g_id = #{gId,jdbcType=BIGINT}


    and mid = #{mid,jdbcType=BIGINT}


    and harmony_id = #{harmonyId,jdbcType=BIGINT}


    and similarity = #{similarity,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from personality_characters_similarity

  is_delete = 0

    and g_id = #{gId,jdbcType=BIGINT}


    and mid = #{mid,jdbcType=BIGINT}


    and harmony_id = #{harmonyId,jdbcType=BIGINT}


    and similarity = #{similarity,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


limit #{pageStartNum},#{pageSize}


select count(*) from personality_characters_similarity

  is_delete = 0

    and g_id = #{gId,jdbcType=BIGINT}


    and mid = #{mid,jdbcType=BIGINT}


    and harmony_id = #{harmonyId,jdbcType=BIGINT}


    and similarity = #{similarity,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}

select t1.*, t3.* from personality_characters_similarity t1, member_wechat t3

  t1.is_delete = 0 and similarity is not null

    and t1.g_id = #{gId,jdbcType=BIGINT}


    and t1.mid = #{mid,jdbcType=BIGINT}


    and t1.similarity = #{similarity,jdbcType=DECIMAL}


    and t1.create_time = #{createTime,jdbcType=TIMESTAMP}


    and t1.modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and t1.is_enable = #{isEnable,jdbcType=TINYINT}

  and t1.mid = t3.mid

order by t1.similarity desc
