mapper.select:

select * from psycho_group_member

  is_delete = 0

    and pg_id = #{pgId,jdbcType=BIGINT}


    and mid = #{mid,jdbcType=BIGINT}


    and statistical_time = #{statisticalTime,jdbcType=TIMESTAMP}


    and eap_advisory_duration = #{eapAdvisoryDuration,jdbcType=BIGINT}


    and eap_advisory_times = #{eapAdvisoryTimes,jdbcType=INTEGER}


    and total_advisory_duration = #{totalAdvisoryDuration,jdbcType=BIGINT}


    and total_advisory_times = #{totalAdvisoryTimes,jdbcType=INTEGER}


    and eap_score = #{eapScore,jdbcType=DECIMAL}


    and common_score = #{commonScore,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and delete_time = #{deleteTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


