mapper.select:
select * from recommend where r_id in
#{item}
and is_delete = 0

mapper.update:

update recommend
set is_delete = 1
where r_id = #{rId,jdbcType=BIGINT}


update recommend
set is_enable = #{enable}
where r_id in

  #{item}

and is_delete = 0

mapper.select:

select * from recommend

  is_delete = 0

    and is_opened = #{isOpened,jdbcType=TINYINT}


    and title = #{title,jdbcType=VARCHAR}


    and cover = #{cover,jdbcType=VARCHAR}


    and detail_link = #{detailLink,jdbcType=VARCHAR}


    and price_name = #{priceName,jdbcType=VARCHAR}


    and price = #{price,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and member_limit = #{memberLimit,jdbcType=INTEGER}


    and price_opened = #{priceOpened,jdbcType=TINYINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from recommend

  is_delete = 0

