#{item}
and is_delete = 0

mapper.update:

update system_resource
set is_delete = 1
where res_id = #{resId,jdbcType=BIGINT}


update system_resource
set is_enable = #{enable}
where res_id in

  #{item}

and is_delete = 0

mapper.select:

select * from system_resource

  is_delete = 0

    and menu_id = #{menuId,jdbcType=BIGINT}


    and res_name = #{resName,jdbcType=VARCHAR}


    and res_url = #{resUrl,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from system_resource

  is_delete = 0

    and menu_id = #{menuId,jdbcType=BIGINT}


    and res_name = #{resName,jdbcType=VARCHAR}


    and res_url = #{resUrl,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and is_enable = #{isEnable,jdbcType=TINYINT}


limit #{pageStartNum},#{pageSize}


select count(*) from system_resource

  is_delete = 0

    and menu_id = #{menuId,jdbcType=BIGINT}


    and res_name = #{resName,jdbcType=VARCHAR}


    and res_url = #{resUrl,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


