mapper.select:

       select * from testing_comment where is_delete='0'


		AND testing_id=#{testingId}


      	AND mid = #{mid}


      	AND test_comment_id = #{testCommentId}


      	AND comment_content = #{commentContent}


      	AND comment_id = #{commentId}


       select * from testing_comment where is_delete = '0'


		AND testing_id=#{testingId}


      	AND mid = #{mid}


      	AND test_comment_id = #{testCommentId}


      	AND comment_id = #{commentId}


      	AND comment_time = #{commentTime}


			order by comment_time desc




			LIMIT #{pageStartNum},#{pageSize}


       select count(*) from testing_comment where is_delete = '0'


		AND testing_id=#{testingId}


      	AND mid = #{mid}


      	AND test_comment_id = #{testCommentId}


      	AND comment_id = #{commentId}


      	AND comment_time = #{commentTime}

mapper.update:
update testing_comment set
comment_content=#{commentContent},
is_delete=#{isDelete},
comment_time=#{commentTime}
where comment_id=#{commentId}
