mapper.select:

select * from testing_result

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and testing_id = #{testingId,jdbcType=BIGINT}


    and questions_id = #{questionsId,jdbcType=BIGINT}


    and options_id = #{optionsId,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


select * from testing_result

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and testing_id = #{testingId,jdbcType=BIGINT}


    and questions_id = #{questionsId,jdbcType=BIGINT}


    and options_id = #{optionsId,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}


limit #{pageStartNum},#{pageSize}


select count(*) from testing_result

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and testing_id = #{testingId,jdbcType=BIGINT}


    and questions_id = #{questionsId,jdbcType=BIGINT}


    and options_id = #{optionsId,jdbcType=BIGINT}


    and is_enable = #{isEnable,jdbcType=TINYINT}

SELECT * FROM ( SELECT tsa.mid AS mid, 0 AS calc, tsa.test_time, tsa.testing_id FROM testing_score_amount tsa WHERE mid IN
#{item}
UNION SELECT t1.mid AS mid, 1 AS calc, t1.test_time, t2.testing_id FROM testing_result_for_member t1, testing_result_for_jump t2 WHERE t1.res_id = t2.res_id AND t1.is_delete = 0 AND t1.mid IN
#{item}
) t3 ORDER BY t3.test_time DESC
limit #{pageStartNum},#{pageSize}

