mapper.update:

update ub_search_words
set is_delete = 1
where usk_id = #{uskId,jdbcType=BIGINT}

mapper.select:

select * from ub_search_words

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and words = #{words,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


select * from ub_search_words

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and words = #{words,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


limit #{pageStartNum},#{pageSize}


select count(*) from ub_search_words

  is_delete = 0

    and mid = #{mid,jdbcType=BIGINT}


    and words = #{words,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}

SELECT usw.* FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN ub_search_words usw ON t1.mid = usw.mid where usw.is_delete=0
and usw.mid=#{mid}
order by create_time desc
limit #{pageStartNum},#{pageSize}

SELECT count(*) FROM ( SELECT DISTINCT (member.mid) mid, member.mobile_phone, member.nickname FROM member, eap_employee WHERE eap_employee.ee_id = #{eeId} AND member.mobile_phone = eap_employee.phone_num AND eap_employee.is_delete = 0 AND member.is_delete = 0
) t1 LEFT JOIN ub_search_words usw ON t1.mid = usw.mid where usw.is_delete=0
